app.list=dad=>{
	app.Dad=dad;
	local=dad.id;
	folder='';
	while(local!=`root`){
		folder=local+'/'+folder;
		local=app.Dads[local].id;
	}
	$('#appFolder').innerHTML='root/'+folder;
	$('#appList').innerHTML="<tr id='appLoad'><td colspan='3' class='w3-center'><img src='load.gif' alt='loading' width='50'></td></tr>";
	let filter=`credential.read;array-contains;${user.displayName}|dad;==;${dad.id}`;
	filter+=$('#appFilterName').value!=''?`|hash;>=;${Hash($('#appFilterName').value)}`:'';
	//console.log(filter);
	read('apps',filter,'hash').then(data=>{
		$('#appList').innerHTML='';
		if(data.length>=0){
			app.Apps=Object.assign(data);
			for(const id in data){
				app.Dads[id]={'id':data[id].dad,'name':dad.name};
				$('#appList').insertAdjacentHTML('beforeend',`<tr>
					<td class='w3-point' id='appFolder${id}'>${data[id].name}</td>
					<td class='w3-w'>${id}</td>
					<td class='w3-right'>
						<button class='w3-btn w3-tiny w3-round w3-blue w3-e' id='appEdit${id}'>
							<span class='w3-w'>Edit</span>
							<span class='w3-y'>E</span>
						</button>
						<button class='w3-btn w3-tiny w3-round w3-red w3-e' id='appDelete${id}'>
							<span class='w3-w'>Delete</span>
							<span class='w3-y'>D</span>
						</button>
					</td>
				</tr>`);
				$("#appFolder"+id).addEventListener('click',()=>app.list({"id":id,"name":data[id].name}));
				$("#appEdit"+id).addEventListener('click',()=>{
					app.Hash=id;
					app.App=Object.assign({},app.Apps[id]);
					app.folder(id);
					app.App.dad=='modules'?show('#modules'):hide('#modules');
					$('#appEditData').reset();
					$('#appCredentialsList').innerHTML='';
					$('#appEditLabel').innerHTML='#'+app.App.hash;
					$('#appEditDad').value=app.App.dad;
					$('#appEditModule').value=app.App.module;
					$('#appEditName').value=app.App.name;
					$('#appEditHTML').value=app.App.html;
					$('#appEditJavascript').value=app.App.javascript;
					app.credentialLoad();
					show('#appEdit');
				});
				$("#appDelete"+id).addEventListener('click',()=>{
					if(confirm('Do you really want to delete the app?')){
						erase('apps/'+id).then(()=>{
							app.list(app.Dad);
							modal('Success','App deleted successfully','green');
						});
					}
				});
			}
			if(dad.id!='root'){
				$('#appList').insertAdjacentHTML('afterbegin',"<tr><td colspan='3' class='w3-point' id='appAbove'>Above</td></tr>");
				$('#appAbove').addEventListener('click',()=>app.list(app.Dads[dad.id]));
			}
