app.credentialSave=data=>{
	if(app.credentialId!='0'){
		let remove=0;
		for(field in data){
			credential=app.App.credential[field.replace('credentialEdit','').toLowerCase()];
			if(data[field]=='1'){
				remove+=1;
				if(!credential.includes(app.credentialId))credential.push(app.credentialId);
			}else{
				if(credential.indexOf(app.credentialId)>=0)credential.splice(credential.indexOf(app.credentialId),1);
			}
		}
		if(remove==0)$('#app.Credential'+app.credentialId).remove();
		app.credentialLoad();
	}
	hide('#appCredentialEdit');
}
