apps.javascript:
app.Groups={};
app.Apps={};
app.App={};
app.Dads=[];
app.Dads['root']={"id":"root","name":"Root"};
app.Dad={"id":"root","name":"Root"};
app.Hash='';

app.filterTime;
$('#appFilterName').addEventListener('keyup',event=>{
	clearTimeout(app.filterTime);
	filterTime=setTimeout(app.List,400,app.Dad);
});
$('#appFilterBtn').addEventListener('click',()=>flip('#appFilter'));

app.list=dad=>{
	app.Dad=dad;
	local=dad.id;
	folder='';
	while(local!=`root`){
		folder=local+'/'+folder;
		local=app.Dads[local].id;
	}
	$('#appFolder').innerHTML='root/'+folder;
	$('#appList').innerHTML="<tr id='appLoad'><td colspan='3' class='w3-center'><img src='load.gif' alt='loading' width='50'></td></tr>";
	let filter=`credential.read;array-contains;${user.displayName}|dad;==;${dad.id}`;
	filter+=$('#appFilterName').value!=''?`|hash;>=;${Hash($('#appFilterName').value)}`:'';
	//console.log(filter);
	read('apps',filter,'hash').then(data=>{
		$('#appList').innerHTML='';
		if(data.length>=0){
			app.Apps=Object.assign(data);
			for(const id in data){
				app.Dads[id]={'id':data[id].dad,'name':dad.name};
				$('#appList').insertAdjacentHTML('beforeend',`<tr>
					<td class='w3-point' id='appFolder${id}'>${data[id].name}</td>
					<td class='w3-w'>${id}</td>
					<td class='w3-right'>
						<button class='w3-btn w3-tiny w3-round w3-blue w3-e' id='appEdit${id}'>
							<span class='w3-w'>Edit</span>
							<span class='w3-y'>E</span>
						</button>
						<button class='w3-btn w3-tiny w3-round w3-red w3-e' id='appDelete${id}'>
							<span class='w3-w'>Delete</span>
							<span class='w3-y'>D</span>
						</button>
					</td>
				</tr>`);
				$("#appFolder"+id).addEventListener('click',()=>app.list({"id":id,"name":data[id].name}));
				$("#appEdit"+id).addEventListener('click',()=>{
					app.Hash=id;
					app.App=Object.assign({},app.Apps[id]);
					app.folder(id);
					app.App.dad=='modules'?show('#modules'):hide('#modules');
					$('#appEditData').reset();
					$('#appCredentialsList').innerHTML='';
					$('#appEditLabel').innerHTML='#'+app.App.hash;
					$('#appEditDad').value=app.App.dad;
					$('#appEditModule').value=app.App.module;
					$('#appEditName').value=app.App.name;
					$('#appEditHTML').value=app.App.html;
					$('#appEditJavascript').value=app.App.javascript;
					app.credentialLoad();
					show('#appEdit');
				});
				$("#appDelete"+id).addEventListener('click',()=>{
					if(confirm('Do you really want to delete the app?')){
						erase('apps/'+id).then(()=>{
							app.list(app.Dad);
							modal('Success','App deleted successfully','green');
						});
					}
				});
			}
			if(dad.id!='root'){
				$('#appList').insertAdjacentHTML('afterbegin',"<tr><td colspan='3' class='w3-point' id='appAbove'>Above</td></tr>");
				$('#appAbove').addEventListener('click',()=>app.list(app.Dads[dad.id]));
			}
		}else{
			$('#appList').innerHTML="<tr id='appLoad'><td colspan='3' class='w3-center'>Empty</td></tr>";
		}
	});
}
app.list({"id":"root","name":"Root"});

$('#appNewBtn').addEventListener('click',()=>{
	app.Hash='';
	app.folder('root');	
	$('#appEditData').reset();
	$('#appEditLabel').innerHTML='';
	$('#appCredentialsList').innerHTML='';
	app.App=Object.assign({	
		"credential":{"write":[user.displayName],"menu":[user.displayName],"read":[user.displayName]},
		"dad":app.Dad.id,
		"hash":"0",
		"module":"none",
		"html":"",
		"javascript":"",
		"name":""
	});
	app.credentialLoad();
	show('#appEdit');
});

$('#appEditName').addEventListener('blur',()=>{
	name=hash($('#appEditName').value);
	if(name!=''){
		read('apps/'+name).then(data=>{
			if(data&&name!=app.App.hash){
				modal('Caution','The chosen name already exists','yellow');
			}else{
				$('#appEditLabel').innerHTML='#'+name;
			}
		});
	}
});

app.save=data=>{
	if(app.App.hash!='0'&&app.Apps[app.App.hash].hash!=hash(data.name))erase('apps/'+app.Apps[app.App.hash].hash);
	app.App.dad=data.dad;
	app.App.name=data.name;
	app.App.hash=hash(data.name);
	app.App.html=data.html;
	app.App.javascript=data.javascript;
	write('apps',app.App,app.App.hash).then(()=>{
		app.App=Object.assign({});
		$('#appEditData').reset();
		hide('#appEdit');
		app.list(app.Dad);
		modal('Success','Data saved successfully','green');
	});
}
form('appEdit',app.save);

app.folder=()=>{
	$('#appEditDad').innerHTML='';
	if(app.Dad.id!='root')$('#appEditDad').innerHTML+=`<option value='${app.Dads[app.Dad.id].id}'>${app.Dads[app.Dad.id].name}`;
	$('#appEditDad').innerHTML+=`<option value='${app.Dad.id}' selected>${app.Dad.name}`;
	for(id in app.Apps)if(app.Apps[id].dad==app.Dad.id&&id!=app.Hash)$('#appEditDad').innerHTML+=`<option value='${id}'>${app.Apps[id].name}`;	
