//Credentials
app.credentialId='0';
app.credentialLoad=()=>{
	$('#appCredentialsList').innerHTML="<figure class='w3-center'><img src='load.gif' width='25'></figure>";
	read('groups','id;in;*'+app.credentialIndex(app.App.credential),'hash').then(data=>{
		app.Groups=Object.assign(data);
		$('#appCredentialsList').innerHTML='';
		for(const id in data){
			$('#appCredentialsList').insertAdjacentHTML('beforeend',`<tr id='appCredential${id}'>
				<td>${data[id].name}</td>
				<td class='w3-right'>
					<button type='button' class='w3-btn w3-tiny w3-round w3-blue w3-e' id='appCredentialEdit${id}'>
						<span class='w3-w'>Edit</span>
						<span class='w3-y'>E</span>
					</button>
					<button type='button' class='w3-btn w3-tiny w3-round w3-red w3-e' id='appCredentialDelete${id}'>
						<span class='w3-w'>Delete</span>
						<span class='w3-y'>D</span>
					</button>
				</td>
			</tr>`);
			$("#appCredentialEdit"+id).addEventListener('click',event=>{
				app.credentialId=id;
				$('#appCredentialEditName').innerHTML=app.Groups[id].name;
				$('#appCredentialEditRead').value=app.App.credential.read.includes(id)?'1':'0';
				$('#appCredentialEditWrite').value=app.App.credential.write.includes(id)?'1':'0';
				$('#appCredentialEditMenu').value=app.App.credential.menu.includes(id)?'1':'0';
				show('#appCredentialEdit');
			});
			$("#appCredentialDelete"+id).addEventListener('click',event=>{
				if(confirm('Do you really want to delete the data?')){
					for(key of ['read','write','menu']){
						credential=app.App.credential[key];
						if(credential.indexOf(id)>=0)credential.splice(credential.indexOf(id),1);
					}
					$('#appCredential'+id).remove();
					modal('Success','Successfully removed','green');
				}
			});
		}
	});	
}

app.credentialIndex=credential=>{
	groupIndex=[].concat(credential.menu,credential.read,credential.write)
	groupIndex=groupIndex.filter((item,pos)=>groupIndex.indexOf(item)===pos);
	return groupIndex.join();
}

$('#appCredentialNew').addEventListener('click',()=>{
	$('#appCredentialEditName').innerHTML='';
	$('#appCredentialEditData').reset();
	show('#appCredentialEdit');	
});

app.credentialSave=data=>{
	if(app.credentialId!='0'){
		let remove=0;
		for(field in data){
			credential=app.App.credential[field.replace('credentialEdit','').toLowerCase()];
			if(data[field]=='1'){
				remove+=1;
				if(!credential.includes(app.credentialId))credential.push(app.credentialId);
			}else{
				if(credential.indexOf(app.credentialId)>=0)credential.splice(credential.indexOf(app.credentialId),1);
			}
		}
		if(remove==0)$('#app.Credential'+app.credentialId).remove();
		app.credentialLoad();
	}
	hide('#appCredentialEdit');
}
form('appCredentialEdit',app.credentialSave);

