join.javascript:
join.user={}

join.change=data=>{
	if(join.user.id=='0'){
		Auth.createUserWithEmailAndPassword(data.email,data.password).then(()=>{
			join.user.password=data.password;
			join.user.id=Auth.currentUser.uid;
			user=Auth.currentUser;
			user.updateProfile({displayName:data.name,photoURL:'common'}).then(()=>{
				user.sendEmailVerification({url:`https://${location.hostname}`}).then(()=>{
					user.updateProfile({displayName:common,photoURL:'common'}).then(()=>{
						show('#joinEditNewPwd');
						join.save(data);
						modal('Success','Your registration was successful.<br><br>Validate your email to activate your access.<br>','green');
					});
				});
			});
		});
	}else{
		Auth.signInWithEmailAndPassword(join.user.email,join.user.password).then(()=>{
			user=Auth.currentUser;
			user.updatePassword(data.newpassword).then(()=>{
				if(data.newpassword!='')join.user.password=data.newpassword;
				user.updateEmail(data.email).then(()=>join.save(data));
			});
		});
		modal('Success','Successfully changed data.<br>','green');
	}
};

join.save=data=>{
	join.user.edited=new Date();
	join.user.name=data.name;
	join.user.hash=hash(data.name);
	join.user.group=common;
	join.user.email=data.email;
	write('users',join.user,join.user.id).then(()=>main('join'));
};

join.check=data=>{
	show('#joinEditLoad');
	hide('#joinEditBtn');
	if(join.user.id=='0'&&($('#joinEditPassword').value!=$('#joinEditCheck').value)||(join.user.id!='0'&&($('#joinEditNewPassword').value!=$('#joinEditNewCheck').value))){
		modal('Caution','Password checks must equal password','yellow');
		show('#joinEditBtn');
		hide('#joinEditLoad');
		return false;
	}
	if(join.user.id=='0'||(join.user.id!='0'&&join.user.email!=data.email)){
		Auth.fetchSignInMethodsForEmail(data.email).then(obj=>{
			if(obj[0]){
				modal('Caution','User already exists','yellow');
				show('#joinEditBtn');
				hide('#joinEditLoad');
			}else{
				join.change(data);
			}
		});
	}else{
		join.change(data);
	}
};

join.start=()=>{
	form('joinEdit',join.check);
	if(user){
		read(`users/${user.uid}`).then(data=>{
			join.user=Object.assign({},data);
			$('#joinEditName').value=data.name;
			$('#joinName').innerHTML=data.name;
			$('#joinEditEmail').value=data.email;
			$('#joinEmail').innerHTML=data.email;
			$('#joinEditTermsOk').required=false;
			$('#joinEditCheck').required=false;
			$('#joinEditUser').addEventListener('click',()=>show('#joinEdit'));
			show('#joinEditNewPwd');
			hide('#joinEditCheck');
			hide('#joinEditTerms');
			hide('#joinLoading');
		});
	}else{
		join.user=Object.assign({},{
			"id":"0",
			"created":new Date(),
			"edited":new Date(),
			"deleted":"0",
			"hash":"",
			"group":"",
			"name":"",
			"email":"",
			"password":""
		});
		show('#joinEditCheck');
		hide('#joinProfile');
		$('#joinEditCheck').required=true;
		$('#joinEditClose').addEventListener('click',()=>location.hash=`#home`);
		$('#joinEditCancel').addEventListener('click',()=>location.hash=`#home`);
		$('#joinEditTermsOk').required=true;
		hide('#joinEditNewPwd');
		show('#joinEditTerms');
		show('#joinEdit');
		read('apps/terms').then(data=>{
			$('#joinEditTerm').innerHTML=data.html;
			hide('#joinLoading');
		});
	}
}

$('#joinDeleteUser').addEventListener('click',()=>{
	if(confirm('Do you really want to delete your information?')){		
		join.user.deleted=new Date();
		write('users',join.user,user.uid).then(()=>{
			Auth.signInWithEmailAndPassword(join.user.email,join.user.password).then(()=>{
				Auth.currentUser.delete().then(()=>{
					hide('#joinLoading');
					join.user={};
					location.hash=`#home`;
					modal('Success','Data deleted successfully','green');
				});
			});
		});
	}	
});

join.start();

login.javascript:
login.logon=data=>{
	hide(`#loginBtn`);
	show(`#loginLoad`);
	Auth.signInWithEmailAndPassword(data.email,data.password).then(data=>{
		user=data.user
		$(`#loginData`).reset();
		show(`#loginBtn`);
		hide(`#loginLoad`);
		hide(`#login`);
		setTimeout(()=>{window.location=`#home`},50);
		modal(`Success`,`Login successfully`,`green`);
	}).catch(error=>{
		show(`#loginBtn`);
		hide(`#loginLoad`);
		modal(`Error`,`Failed to login`,`red`);
	})
}

login.logoff=()=>{
	Auth.signOut().then(()=>{
		user=null;
		setTimeout(()=>{window.location=`#home`},50);
		modal(`Success`,`Successful logoff`,`green`);
	}).catch(error=>{
		console.log(error);
	})
}

form(`login`,login.logon);

