	}
	if(users.User.id=='0'||(users.User.id!='0'&&users.User.email!=data.email)){
		Auth.fetchSignInMethodsForEmail(data.email).then(obj=>{
			if(obj[0]){
				modal('Caution','User already exists','yellow');
				show('#usersEditBtn');
				hide('#usersEditLoad');
			}else{
				users.change(data);
			}
		});
	}else{
		users.change(data);
	}
}

users.change=data=>{
	read(`users/${user.uid}`).then(adm=>{
		users.email=adm.email;
		users.password=adm.password;
		if(users.User.id=='0'){
			Auth.createUserWithEmailAndPassword(data.email,data.password).then(()=>{
				users.User.id=Auth.currentUser.uid;
				Auth.currentUser.updateProfile({displayName:users.User.group,photoURL:users.Groups[data.group].hash}).then(()=>Auth.signInWithEmailAndPassword(users.email,users.password).then(()=>users.save(data)));
			});
		}else{
			Auth.signInWithEmailAndPassword(users.User.email,users.User.password).then(()=>Auth.currentUser.updateProfile({displayName:data.group,photoURL:users.Groups[data.group].hash}).then(()=>Auth.currentUser.updateEmail(data.email).then(()=>Auth.currentUser.updatePassword(data.password==''?users.User.password:data.password).then(()=>Auth.signInWithEmailAndPassword(users.email,users.password).then(()=>users.save(data))))));
		}
	});
}

users.save=data=>{
	users.email=null;
	users.password=null;
	users.User.edited=new Date();
	users.User.name=data.name;
	users.User.hash=hash(data.name);
	users.User.group=data.group;
	users.User.email=data.email;
	if(data.password!='')users.User.password=data.password;
	write('users',users.User,users.User.id).then(()=>{
		users.list();
		show('#usersEditBtn');
		hide('#usersEditLoad');
		hide('#usersEdit');
		modal('Success','User created','green');
	});
}
